// App.js
import React, { useContext } from 'react';
import { LanguageContext } from './LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import './App.css';

const App = () => {
  const { language } = useContext(LanguageContext);
  const translations = require(`./translations/${language}.json`);

  return (
    <Router>
      <div className="app">
        <Switch>
          <Route path="/:lang" render={({ match }) => {
            const langParam = match.params.lang;
            const languages = ['en', 'es', 'fr', 'de', 'hi', 'ml', 'ta', 'pa', 'as', 'bn'];

            if (languages.includes(langParam)) {
              return (
                <div className="card">
                  <header>
                    <h1 className="title">{translations.greeting}</h1>
                    <p className="subtitle">{translations.message}</p>
                  </header>
                </div>
              );
            } else {
              // Redirect to the default language if the URL language is invalid
              return <Redirect to={`/${language}`} />;
            }
          }} />
          <Redirect exact from="/" to={`/${language}`} />
        </Switch>
        <LanguageSelector />
      </div>
    </Router>
  );
};

export default App;
